mapper.select:
SELECT goodInfo.* , category_name FROM tb_newbee_mall_goods_info goodInfo, tb_newbee_mall_goods_category cate WHERE 1=1
and goodInfo.goods_name like CONCAT('%',#{goodsName},'%')
and goodInfo.goods_category_id = cate.category_id limit #{limit}, #{pageSize}

select * from tb_newbee_mall_goods_info
goods_name like concat('%', #{goodsName}, '%')
order by create_time desc limit #{limit}, #{pageSize}

